        if ($info -match "Name: docker-desktop") {
            Write-ContainerLog "Docker Engine is ready"
            break
        }
    }
    catch {
        # Continue waiting
    }

    if ([datetime]::Now -gt $engineTimeout) {
        Write-ContainerLog "ERROR: Docker Engine failed to initialize"
